SELECT 
    ${config.solutions}.program_name AS "Program Name",
    ${config.projects}.state_name AS "State Name",
    SUM(CASE WHEN ${config.projects}.status = 'started' THEN 1 ELSE 0 END) AS "Started",
    SUM(CASE WHEN ${config.projects}.status = 'inProgress' THEN 1 ELSE 0 END) AS "In-Progress",
	SUM(CASE WHEN ${config.projects}.status = 'submitted' THEN 1 ELSE 0 END) AS "Submitted",
    SUM(CASE WHEN ${config.projects}.status = 'submitted' AND ${config.projects}.evidence IS NOT NULL THEN 1 ELSE 0 END) AS "Submitted projects with evidences"
FROM ${config.projects} 
JOIN ${config.solutions} ON ${config.projects}.solution_id = ${config.solutions}.solution_id
WHERE 1 = 1
AND ${config.projects}.state_name is not null 
AND ${config.solutions}.program_name is not null 
[[AND ${config.projects}.state_id = (
        SELECT state_id
        FROM ${config.projects}
        WHERE {{state_param}}
        LIMIT 1)
  ]]
  [[AND ${config.projects}.district_id = (
        SELECT district_id
        FROM ${config.projects}
        WHERE {{district_param}} 
        LIMIT 1)
  ]] 
  [[AND ${config.projects}.program_id = (
        SELECT ${config.projects}.program_id
        FROM ${config.projects}
        JOIN ${config.solutions} ON ${config.projects}.solution_id = ${config.solutions}.solution_id
        WHERE {{program_param}} 
        LIMIT 1 )
   ]]
GROUP BY ${config.solutions}.program_name, ${config.projects}.state_name
ORDER BY "Submitted" DESC
